import React from 'react'
import "./Chartjs.css"
import { Bar, Line, Doughnut } from "react-chartjs-2";
import { useState, useEffect} from 'react';
import axios from "axios";



const Chartjs = () => {
    const [chartData, setChartData] = useState([]);
        const [casConfirmes, setcasConfirmes] = useState([]);
             const [hospitalises, sethospitalises] = useState([]);


    const chart = () => {
         let casConf = [];
         let hospit = [];
         let date = [];
         let dece = [];

    axios
      .get("https://coronavirusapi-france.vercel.app/AllLiveData")
      .then(res => {
          // setcovidData(res.data)
        console.log(res);
        for (const dataObj of res.data.allLiveFranceData) {
            //console.log(dataObj)
          casConf.push(parseInt(dataObj.casConfirmes));
          hospit.push(parseInt(dataObj.hospitalises));
          date.push(dataObj.date)
          dece.push(dataObj.deces)

        }
         //console.log(casConf,hospit)
         const labels = Utils.months({count: 7});
        setChartData({
          labels: Utils.months({count: 7}),
          datasets: [
            {
              label: "Hospitalitation",
              data: hospit,
              backgroundColor: ["red"],
              borderColor: 'rgba(255, 99, 132, 0.2)',
              borderWidth: 4
            },
            {
              label: "mort",
              data: dece,
              backgroundColor: ["green"],
              borderColor: 'bleu',
              borderWidth: 4
            },
          ]
        });
        setcasConfirmes({
          labels: dece,
          datasets: [
            {
              label: "Décés Humains",
              data: dece,
              backgroundColor: ["black","red"],
              borderWidth: 2
            },
          ]
        });
       sethospitalises({
          labels: ['dece','hospit'],
          datasets: [
            {
              label: "Décés Humains",
              data: dece,hospit,
              backgroundColor: ["rgba(75, 192, 192, 0.6)"],
              borderWidth: 4
            },
          ]
        });

      })
      .catch(err => {
       // console.log(err);
      });
    // console.log(casConf, hospit);
  };

  useEffect(() => {
    chart();
    }, []);



      const options = {
          maintainAspectRatio: true,
        // scales: {
        // x: {
        //   type: 'time',
        //   display: true,
        //   title: {
        //     display: true,
        //     text: 'Date'
        //   },
        //   ticks: {
        //     major: {
        //       enabled: true
        //     },
        //   }
        //   }  
        // }
      }



// function Chartjs() {
    return (
        <div className="container">

            <div className="card">
            <Bar data ={chartData} options={options} />
            </div>

            <div className="card">
            <Line data ={casConfirmes} options={options} />
            </div>

        </div>
    )
}

export default Chartjs
