import React from 'react'
import {Link} from 'react-router-dom'
import "./Navbar.css"
import Darkmod from './Darkmod'
import {useState} from 'recat';

function Navbar() {

    const [showLinks, setShowLinks] = useState(false)

    const handleShowLinks = () => {
        setShowLinks(!showLinks)
    }
    return (   
        <div className={`navbar ${showLinks ? "navbar show-nav" : "navbar hide-nav"} `}>
            <Link to="/" className="navbar_link navbar_item slideInDown-1" className="link">INFOS</Link>
            <Link to="/graphiques"className="navbar_link navbar_item slideInDown-2" className="link">GRAPHIQUES</Link>
            <Link to="/centres" className="navbar_link navbar_item slideInDown-3" className="link">CENTRES DE VACCINATION</Link>
            <Link to="/quizz"className="navbar_link" className="navbar_item slideInDown-4" target="_blank" className="link">QUIZZ</Link>
            <Darkmod/>
        <button className="navbar_burger" onClick = {handleShowLinks}>
            <span className="burger-bar"></span>
        </button>
       </div>

    )
}

export default Navbar
